import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import TravelList from './travelList';

function MainPage({ travel, deleteTravel, fetch }) {
  const [query, setQuery] = useState('');
  const [filteredTravel, setFilteredTravel] = useState(travel); // Default to showing all books
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);
  const [hasMobileButton, setHasMobileButton] = useState(false);

  const navRef = useRef(null);
  const mobileNavItemsRef = useRef([]);
  const mobileNavTriggersRef = useRef([]);

  const navigate = useNavigate();

  const isOverflown = ({ clientWidth, clientHeight, scrollWidth, scrollHeight }) => {
    return scrollHeight > clientHeight || scrollWidth > clientWidth;
  };

  const isVisible = (parent, child) => {
    return !(
      (child.offsetLeft - parent.offsetLeft >= parent.offsetWidth) ||
      (child.offsetTop - parent.offsetTop >= parent.offsetHeight)
    );
  };

  const handleSearch = () => {
    if (!query) {
      setFilteredTravel(travel);
      return;
    }
    const lowerQuery = query.toLowerCase();
    const filtered = travel.filter((travel) => {
      return (
        travel.title.toLowerCase().includes(lowerQuery) ||
        travel.story.toLowerCase().includes(lowerQuery)
      );
    });

    setFilteredTravel(filtered);
  };

  useEffect(() => {
    handleSearch();
  }, [query, travel]);

  useEffect(() => {
    const nav = navRef.current;
    const mobileNavItems = mobileNavItemsRef.current;

    const showMobileNavButton = () => {
      const resizeObserver = new ResizeObserver(() => {
        const isNavOverflowing = isOverflown(nav);
        setHasMobileButton(isNavOverflowing);

        mobileNavItems.forEach((item) => {
          const matchingNavItem = mobileNavItemsRef.current.find(
            (el) => el.dataset.mobileNavItem === item.dataset.navItem
          );
          matchingNavItem.classList.toggle('is-visible', !isVisible(nav, item));
        });
      });

      resizeObserver.observe(nav);
    };

    showMobileNavButton();
  }, []);

  const toggleMobileNav = () => {
    setIsMobileNavOpen((prev) => !prev);
  };

  const closeMobileNav = () => {
    setIsMobileNavOpen(false);
  };

  return (
    <div>
      {/* Header with mobile nav integration */}
      <nav className="navbar navbar-expand-lg navbar-light bg-light custom-navbar">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">Travel Story</a>
          <button
            className="navbar-toggler"
            type="button"
            onClick={toggleMobileNav}
            aria-controls="navbarNav"
            aria-expanded={isMobileNavOpen ? 'true' : 'false'}
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className={`collapse navbar-collapse ${isMobileNavOpen ? 'show' : ''}`} id="navbarNav">
            <ul className="navbar-nav ms-auto">
              {['Home', 'Add Your Story', 'Logout'].map((item, index) => (
                <li key={index} className="nav-item">
                  <a
                    className="nav-link"
                    href="#"
                    onClick={(e) => {
                      e.preventDefault();
                      if (item === 'Home') navigate('/');
                      if (item === 'Add Your Story') navigate('/add');
                      if (item === 'Logout') navigate('/login');
                    }}
                  >
                    {item}
                  </a>
                </li>
              ))}
            </ul>
            <form className="d-flex ms-auto" role="search" onSubmit={(e) => e.preventDefault()}>
              <input
                className="form-control me-2"
                type="search"
                placeholder="Search by title, story, or other fields"
                aria-label="Search"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
              />
              <button className="btn btn-outline-success" type="button" onClick={handleSearch}>Search</button>
            </form>
          </div>
        </div>
      </nav>

      {/* Mobile Navigation */}
      <div className={`mobile-nav-overlay ${isMobileNavOpen ? 'is-mobilenav-open' : ''}`} onClick={closeMobileNav}></div>
      <div className={`mobile-nav ${isMobileNavOpen ? 'is-mobilenav-open' : ''}`}>
        <ul className="mobile-nav-list">
          {['Home', 'Add Your Story', 'Logout'].map((item, index) => (
            <li key={index} className="mobile-nav-item">
              <a
                href="#"
                onClick={(e) => {
                  e.preventDefault();
                  if (item === 'Home') navigate('/');
                  if (item === 'Add Your Story') navigate('/add');
                  if (item === 'Logout') navigate('/login');
                }}
              >
                {item}
              </a>
            </li>
          ))}
        </ul>
      </div>

      {/* Travel List */}
      <TravelList travel={filteredTravel} deleteTravel={deleteTravel} />
    </div>
  );
}

export default MainPage;
