import React, { useState, useEffect } from "react";
import { FaCalendar, FaSearch, FaStar } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import axios from "axios";  // Import axios
import TravelStoryCard from './TravelStoryCard'

const Home = ({travel,deleteTravel,fetch}) => {
  const navigate = useNavigate();
  const [stories, setStories] = useState([]);
  const [filteredStories, setFilteredStories] = useState([]);  // Store filtered stories
  const [favoriteStories, setFavoriteStories] = useState([]);
  const [recentStories, setRecentStories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = () => {
    if (!query) {
      setFilteredBooks(books);
      return;
    }
    const lowerQuery = query.toLowerCase();
    const filtered = books.filter((book) => {
      return (
        book.title.toLowerCase().includes(lowerQuery) ||
        book.author.toLowerCase().includes(lowerQuery) ||
        book.description.toLowerCase().includes(lowerQuery) ||
        book.category.some((cat) => cat.toLowerCase().includes(lowerQuery))
      );
    });

    setFilteredBooks(filtered);
  };

  useEffect(() => {
    handleSearch();
  }, [query, books]); 
  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="relative bg-blue-600 text-white py-20">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl font-bold mb-4">Travel Story App</h1>
          <p className="text-xl mb-8">Share your amazing travel experiences</p>
          
          {/* Search Bar */}
          <div className="flex justify-center items-center gap-2 max-w-md mx-auto">
            <input
              type="text"
              className="w-full px-4 py-2 rounded-lg text-gray-900"
              placeholder="Search stories..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button
              onClick={handleSearch}
              className="bg-white text-blue-600 p-2 rounded-lg hover:bg-gray-100"
            >
              <FaSearch className="text-xl" />
            </button>
          </div>

          {/* Filter Buttons */}
          <div className="flex justify-center gap-4 mt-8">
            <button
              onClick={() => navigate("/stories/favorite")}
              className="flex items-center gap-2 bg-white bg-opacity-20 px-4 py-2 rounded-lg hover:bg-opacity-30"
            >
              <FaStar />
              Favorites
            </button>
            <button
              onClick={() => navigate("/stories/recent")}
              className="flex items-center gap-2 bg-white bg-opacity-20 px-4 py-2 rounded-lg hover:bg-opacity-30"
            >
              <FaCalendar />
              Recent
            </button>
          </div>
        </div>
      </div>

      {/* Content Section */}
      <div className="container mx-auto px-4 py-8">
        {loading ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-600 mx-auto"></div>
          </div>
        ) : (
          <div className="space-y-12">
            {/* Favorite Stories */}
            {favoriteStories.length > 0 && (
              <section>
                <h2 className="text-2xl font-bold mb-4">Favorite Stories</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {favoriteStories.map((story) => (
                    <TravelStoryCard 
                      key={story.id} 
                      story={story}
                      onUpdate={fetchStories}
                    />
                  ))}
                </div>
              </section>
            )}

            {/* Recent Stories */}
            {recentStories.length > 0 && (
              <section>
                <h2 className="text-2xl font-bold mb-4">Recent Stories</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {recentStories.map((story) => (
                    <TravelStoryCard 
                      key={story.id} 
                      story={story}
                      onUpdate={fetchStories}
                    />
                  ))}
                </div>
              </section>
            )}

            {/* Filtered Stories */}
            {filteredStories.length > 0 && (
              <section>
                <h2 className="text-2xl font-bold mb-4">All Stories</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {filteredStories.map((story) => (
                    <TravelStoryCard 
                      key={story.id} 
                      story={story}
                      onUpdate={fetchStories}
                    />
                  ))}
                </div>
              </section>
            )}

            {filteredStories.length === 0 && (
              <div className="text-center py-8">
                <p className="text-gray-500">No stories found. Create your first story!</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
