import React from "react";
import { FaHeart, FaRegHeart, FaEdit, FaTrash } from "react-icons/fa";

const TravelStoryCard = ({ story, onFavoriteToggle, onDelete, onEdit }) => {
  const { id, title, visitedLocation, imageUrl, visitedDate, isFavourite, story: storyText } = story;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
      {/* Image Section */}
      <div className="relative">
        <img
          src={imageUrl}
          alt={title || "Travel Story"}
          className="w-full h-48 object-cover hover:scale-105 transition-transform"
        />
        {isFavourite && (
          <span className="absolute top-2 right-2 bg-red-500 text-white text-xs font-semibold py-1 px-2 rounded">
            Favourite
          </span>
        )}
      </div>

      {/* Content Section */}
      <div className="p-4">
        {/* Title and Location */}
        <h3 className="text-xl font-semibold mb-2 line-clamp-1">{title || "Untitled Story"}</h3>
        <p className="text-gray-600 mb-2">{visitedLocation || "Unknown Location"}</p>

        {/* Date */}
        <p className="text-sm text-gray-500 mb-4">
          {visitedDate ? new Date(visitedDate).toLocaleDateString() : "Date not available"}
        </p>

        {/* Story Text */}
        <p className="text-gray-700 line-clamp-3 mb-4">{storyText || "No story provided."}</p>

        {/* Actions */}
        <div className="flex justify-between items-center">
          {/* Favorite Toggle */}
          <button
            onClick={() => onFavoriteToggle(id, !isFavourite)}
            aria-label={isFavourite ? "Remove from favorites" : "Add to favorites"}
            className="text-red-500 hover:text-red-600 focus:outline-none"
          >
            {isFavourite ? <FaHeart size={20} /> : <FaRegHeart size={20} />}
          </button>

          {/* Edit and Delete Actions */}
          <div className="flex gap-3">
            <button
              onClick={() => onEdit(id)}
              aria-label="Edit story"
              className="text-blue-500 hover:text-blue-600 focus:outline-none"
            >
              <FaEdit size={20} />
            </button>
            <button
              onClick={() => onDelete(id)}
              aria-label="Delete story"
              className="text-gray-500 hover:text-red-500 focus:outline-none"
            >
              <FaTrash size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TravelStoryCard;
