import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function TravelList({ travel, deleteTravel }) {
  const navigate = useNavigate();

  const [mouseX, setMouseX] = useState(0);
  const [mouseY, setMouseY] = useState(0);
  const [mouseLeaveDelay, setMouseLeaveDelay] = useState(null);

  const cardRef = useRef(null);

  // Card size
  const width = cardRef.current ? cardRef.current.offsetWidth : 0;
  const height = cardRef.current ? cardRef.current.offsetHeight : 0;

  // Mouse position relative to card size
  const mousePX = mouseX / width;
  const mousePY = mouseY / height;

  // Card style for 3D effect
  const cardStyle = {
    transform: `rotateY(${mousePX * 30}deg) rotateX(${mousePY * -30}deg)`
  };

  // Background image transform
  const cardBgTransform = {
    transform: `translateX(${mousePX * -40}px) translateY(${mousePY * -40}px)`
  };

  // Event handlers
  useEffect(() => {
    if (cardRef.current) {
      const handleMouseMove = (e) => {
        setMouseX(e.pageX - cardRef.current.offsetLeft - width / 2);
        setMouseY(e.pageY - cardRef.current.offsetTop - height / 2);
      };

      const handleMouseEnter = () => {
        clearTimeout(mouseLeaveDelay);
      };

      const handleMouseLeave = () => {
        const delay = setTimeout(() => {
          setMouseX(0);
          setMouseY(0);
        }, 1000);
        setMouseLeaveDelay(delay);
      };

      cardRef.current.addEventListener('mousemove', handleMouseMove);
      cardRef.current.addEventListener('mouseenter', handleMouseEnter);
      cardRef.current.addEventListener('mouseleave', handleMouseLeave);

      return () => {
        clearTimeout(mouseLeaveDelay);
        cardRef.current.removeEventListener('mousemove', handleMouseMove);
        cardRef.current.removeEventListener('mouseenter', handleMouseEnter);
        cardRef.current.removeEventListener('mouseleave', handleMouseLeave);
      };
    }
  }, [mouseLeaveDelay, width, height]);

  return (
    <div className="page-content">
      {travel.map((el) => (
        <div className="card-wrap" key={el.id} ref={cardRef}>
          <div className="card" style={cardStyle}>
            <div className="card-bg" style={{ ...cardBgTransform, backgroundImage: `url(${el.imageUrl})` }} />
            <div className="card-info">
              <h2 className="title">{el.title}</h2>
              <p className="copy">{el.story}</p>
              <p className="copy">{el.isFavourite}</p>
              <button className="btn" onClick={() => navigate("/details")}>
                View Trips
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default TravelList;
