import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const navigate = useNavigate();

  const [loginData, setLoginData] = useState({ email: "", password: "" });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8000/api/user/login", loginData);
      localStorage.setItem("token", response.data.token);
      navigate("/main");
    } catch (error) {
      console.error("Login failed:", error);
      alert("Invalid credentials. Please try again.");
    }
  };

  return (
    <div
      className="container-fluid vh-100 d-flex align-items-center justify-content-center"
      style={{
        backgroundImage: "url('https://wallpapercave.com/wp/wp4782899.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        position: "relative",
      }}
    >
      <div
        className="position-absolute w-100 h-100"
        style={{
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          top: 0,
          left: 0,
        }}
      ></div>

      <div
        className="row w-100 rounded shadow"
        style={{ maxWidth: "900px", zIndex: 2, backgroundColor: "#ffffff", borderRadius: "20px" }}
      >
        {/* Left Section */}
        <div
          className="col-lg-6 d-none d-lg-flex align-items-center justify-content-center"
          style={{
            backgroundImage: "url('https://wallpapercave.com/wp/wp4782899.jpg')",
            backgroundSize: "cover",
            backgroundPosition: "center",
            borderTopLeftRadius: "20px",
            borderBottomLeftRadius: "20px",
          }}
        >
          <div className="text-center text-white p-4">
            <h1 className="fw-bold mb-3">Join the Adventure</h1>
            <p>Create an account to document your travels and memories.</p>
          </div>
        </div>

        {/* Right Section */}
        <div className="col-lg-6 p-5">
          <h4 className="text-center fw-bold mb-4">Login</h4>
          <form onSubmit={handleLogin}>
            {/* Email Input */}
            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                className="form-control rounded-pill"
                placeholder="Enter your email"
                value={loginData.email}
                onChange={handleInputChange}
              />
            </div>

            {/* Password Input */}
            <div className="mb-4">
              <label htmlFor="password" className="form-label">
                Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                className="form-control rounded-pill"
                placeholder="Enter your password"
                value={loginData.password}
                onChange={handleInputChange}
              />
            </div>

            {/* Login Button */}
            <div className="d-grid mb-3">
              <button
                type="submit"
                className="btn btn-lg text-white rounded-pill"
                style={{ background: "#00796b" }}
              >
                LOGIN
              </button>
            </div>

            {/* Divider */}
            <p className="text-center my-3 text-muted">Or</p>

            {/* Create Account Button */}
            <div className="d-grid">
              <button
                type="button"
                className="btn btn-lg rounded-pill"
                style={{
                  backgroundColor: "#e0f2f1",
                  border: "2px solid #00796b",
                  color: "#00796b",
                }}
                onClick={() => navigate("/signUp")}
              >
                CREATE ACCOUNT
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
