import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

function UpdateTravel({ fetch }) {
    const { state } = useLocation();
    const travel = state?.travel;

    const [title, setTitle]= useState(travel?.title || '');
    const [imageUrl, setimageUrl] = useState(travel?.imageUrl ||'')
    const [story, setstory] = useState(travel?.story || '');
    const [visitedDate, setvisitedDate] = useState(travel?.visitedDate || '');

    const navigate = useNavigate();

    const updatebooks = () => {
        const token = localStorage.getItem('token');
        axios.put(`http://localhost:8000/api/travel/edit/${travel.id}`, {
            title,
            story,
            imageUrl,
            visitedDate,
        }, {
            headers: {
                Authorization: `Bearer ${token}`,
            },
        })
        .then(() => {
            console.log("Travel updated");
            fetch();
            navigate("/main");
        })
        .catch(error => {
            console.error(error);
        });
    };
return (
        <div className="container">
            <h2>Update Book</h2>
            <form>
                {/* Input fields for each property */}
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" />
                <input type="text" value={story} onChange={(e) => setstory(e.target.value)} placeholder="story" />
                <input type="text" value={imageUrl} onChange={(e) => setimageUrl(e.target.value)} placeholder="imageUrl" />
                <input type="number" value={visitedDate} onChange={(e) => setvisitedDate(e.target.value)} placeholder="visitedDate" />
                <button type="button" onClick={updatebooks}>Update travel</button>
            </form>
        </div>
    );
}

export default UpdateTravel;
