import React from "react";
import { FaHeart, FaRegHeart, FaEdit, FaTrash } from "react-icons/fa";
import { travelAPI } from "../services/api";

const TravelStoryCard = ({ story, onUpdate }) => {
  const { id, title, visitedLocation, imageUrl, visitedDate, isFavourite, story: storyText } = story;

  const handleFavorite = async () => {
    try {
      await travelAPI.toggleFavorite(id, !isFavourite);
      onUpdate();
    } catch (error) {
      console.error("Error toggling favorite:", error);
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this story?")) {
      try {
        await travelAPI.deleteStory(id);
        onUpdate();
      } catch (error) {
        console.error("Error deleting story:", error);
      }
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
      <img 
        src={imageUrl} 
        alt={title}
        className="w-full h-48 object-cover"
      />
      
      <div className="p-4">
        <h3 className="text-xl font-semibold mb-2">{title}</h3>
        <p className="text-gray-600 mb-2">{visitedLocation}</p>
        <p className="text-sm text-gray-500 mb-4">
          {new Date(visitedDate).toLocaleDateString()}
        </p>
        <p className="text-gray-700 line-clamp-3 mb-4">{storyText}</p>

        <div className="flex justify-between items-center">
          <button
            onClick={handleFavorite}
            className="text-red-500 hover:text-red-600"
          >
            {isFavourite ? <FaHeart size={20} /> : <FaRegHeart size={20} />}
          </button>

          <div className="flex gap-3">
            <button
              onClick={() => onUpdate(story)}
              className="text-blue-500 hover:text-blue-600"
            >
              <FaEdit size={20} />
            </button>
            <button
              onClick={handleDelete}
              className="text-gray-500 hover:text-red-500"
            >
              <FaTrash size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TravelStoryCard;
