import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const navigate = useNavigate();
  const [loginData, setLoginData] = useState({ email: "", password: "" });
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/api/user/login", loginData);
      localStorage.setItem("token", response.data.token);
      setIsAuthenticated(true);
      navigate("/main");  // Redirect to the main page after successful login
    } catch (error) {
      console.error("Login failed:", error);
    }
  };

  return (
    <div
      className="d-flex vh-100 align-items-center justify-content-center"
      style={{ backgroundColor: "#e0f7fa" }} // Light cyan background
    >
      <div
        className="shadow-lg rounded p-4"
        style={{
          maxWidth: "350px",
          width: "100%",
          backgroundColor: "white",
          borderRadius: "15px",
        }}
      >
        <h4 className="text-center fw-bold mb-4">Login</h4>
        <form onSubmit={handleLogin}>
          {/* Email Input */}
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              id="email"
              className="form-control"
              placeholder="Enter your email"
              value={loginData.email}
              onChange={(e) =>
                setLoginData({ ...loginData, email: e.target.value })
              }
            />
          </div>

          {/* Password Input */}
          <div className="mb-4">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <div className="input-group">
              <input
                type="password"
                id="password"
                className="form-control"
                placeholder="Enter your password"
                value={loginData.password}
                onChange={(e) =>
                  setLoginData({ ...loginData, password: e.target.value })
                }
              />
              <button
                className="btn btn-outline-secondary"
                type="button"
                id="togglePassword"
              >
                üëÅÔ∏è {/* Replace with an icon */}
              </button>
            </div>
          </div>

          {/* Login Button */}
          <div className="d-grid">
            <button
              type="submit"
              className="btn"
              style={{
                backgroundColor: "#00bcd4",
                color: "white",
                borderRadius: "10px",
              }}
            >
              LOGIN
            </button>
          </div>

          {/* Divider */}
          <p className="text-center my-3 text-muted">Or</p>

          {/* Create Account Button */}
          <div className="d-grid">
            <button
              type="button"
              className="btn"
              style={{
                backgroundColor: "#b2ebf2",
                color: "#00bcd4",
                borderRadius: "10px",
              }}
              onClick={() => navigate("/register")} // Redirect to registration page
            >
              CREATE ACCOUNT
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
