import { useState } from 'react';

function UpdateTravelForm() {
  const [title, setTitle] = useState('Default Title'); // Default value for Title
  const [story, setStory] = useState('Default Description'); // Default value for Story
  const [imageUrl, setImageUrl] = useState('https://via.placeholder.com/150'); // Default value for Image URL
  const [visitedDate, setVisitedDate] = useState('2025-01-01'); // Default value for Visited Date

  const updatebooks = () => {
    // Function to handle the form submission logic
    console.log('Form submitted with data:', { title, story, imageUrl, visitedDate });
  };

  return (
    <div
      className="container-fluid vh-100 d-flex align-items-center justify-content-center"
      style={{
        backgroundImage: "url('https://wallpapercave.com/wp/wp4782899.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        position: "relative",
      }}
    >
      <div
        className="shadow-lg bg-white rounded p-4"
        style={{
          width: "90%",
          maxWidth: "450px",
          padding: "30px",
        }}
      >
        <h4 className="text-center fw-bold mb-4 text-primary">Update Travel</h4>
        <form className="custom-form">
          {/* Title Input */}
          <div className="mb-4">
            <label htmlFor="title" className="form-label fw-semibold text-muted">
              Title
            </label>
            <input
              type="text"
              id="title"
              className="form-control shadow-sm"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>

          {/* Description Input */}
          <div className="mb-4">
            <label htmlFor="story" className="form-label fw-semibold text-muted">
              Description
            </label>
            <input
              type="text"
              id="story"
              className="form-control shadow-sm"
              placeholder="Description"
              value={story}
              onChange={(e) => setStory(e.target.value)}
            />
          </div>

          {/* Cover Image URL Input */}
          <div className="mb-4">
            <label htmlFor="coverImage" className="form-label fw-semibold text-muted">
              Cover Image URL
            </label>
            <input
              type="text"
              id="coverImage"
              className="form-control shadow-sm"
              placeholder="Cover Image URL"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
            />
          </div>

          {/* Visited Date Input */}
          <div className="mb-4">
            <label htmlFor="visitedDate" className="form-label fw-semibold text-muted">
              Visited Date
            </label>
            <input
              type="date"
              id="visitedDate"
              className="form-control shadow-sm"
              value={visitedDate}
              onChange={(e) => setVisitedDate(e.target.value)}
            />
          </div>

          {/* Update Travel Button */}
          <div className="d-grid">
            <button
              type="button"
              className="btn btn-lg text-white"
              style={{
                background: "#00bcd4",
                borderRadius: "25px",
                transition: "transform 0.2s",
              }}
              onMouseOver={(e) => {
                e.target.style.background = "#00a1c1";
                e.target.style.transform = "scale(1.05)";
              }}
              onMouseOut={(e) => {
                e.target.style.background = "#00bcd4";
                e.target.style.transform = "scale(1)";
              }}
              onClick={updatebooks}
            >
              Update Travel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default UpdateTravelForm;
