import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';

function TravelList({ travel, deleteTravel }) {
  const navigate = useNavigate();
  
  // Refs to store dimensions and mouse position
  const cardRef = useRef(null);
  const mousePositionRef = useRef({ x: 0, y: 0 });
  
  // Store width and height of the card (calculated only once on mount)
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });

  useEffect(() => {
    if (cardRef.current) {
      setDimensions({
        width: cardRef.current.offsetWidth,
        height: cardRef.current.offsetHeight,
      });
    }
  }, []);

  // Mouse position calculation
  const handleMouseMove = useCallback((e) => {
    if (cardRef.current) {
      mousePositionRef.current.x = e.pageX - cardRef.current.offsetLeft - dimensions.width / 2;
      mousePositionRef.current.y = e.pageY - cardRef.current.offsetTop - dimensions.height / 2;
    }
  }, [dimensions]);

  const handleMouseEnter = useCallback(() => {
    clearTimeout(mouseLeaveDelay.current);
  }, []);

  const handleMouseLeave = useCallback(() => {
    mouseLeaveDelay.current = setTimeout(() => {
      mousePositionRef.current.x = 0;
      mousePositionRef.current.y = 0;
    }, 1000);
  }, []);

  useEffect(() => {
    const cardElement = cardRef.current;

    if (cardElement) {
      cardElement.addEventListener('mousemove', handleMouseMove);
      cardElement.addEventListener('mouseenter', handleMouseEnter);
      cardElement.addEventListener('mouseleave', handleMouseLeave);

      return () => {
        cardElement.removeEventListener('mousemove', handleMouseMove);
        cardElement.removeEventListener('mouseenter', handleMouseEnter);
        cardElement.removeEventListener('mouseleave', handleMouseLeave);
      };
    }
  }, [handleMouseMove, handleMouseEnter, handleMouseLeave]);

  // Card transform styles
  const cardStyle = {
    transform: `rotateY(${(mousePositionRef.current.x / dimensions.width) * 30}deg) rotateX(${(mousePositionRef.current.y / dimensions.height) * -30}deg)`
  };

  const cardBgTransform = {
    transform: `translateX(${(mousePositionRef.current.x / dimensions.width) * -40}px) translateY(${(mousePositionRef.current.y / dimensions.height) * -40}px)`
  };

  return (
    <div className="page-content">
      {travel.map((el) => (
        <div className="card-wrap" key={el.id} ref={cardRef}>
          <div className="card" style={cardStyle}>
            <div className="card-bg" style={{ ...cardBgTransform, backgroundImage: `url(${el.imageUrl})` }} />
            <div className="card-info">
              <h2 className="title">{el.title}</h2>
              <p className="copy">{el.story}</p>
              <p className="copy">{el.isFavourite}</p>
              <button className="btn" onClick={() => navigate("/details")}>
                View Trips
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default TravelList;
